var name = msg.topic;
var value = msg.payload;  //The value to check (Must be a number)
var valueText = msg.valueText || value; //Text formatted value (or the value itself if valueText is undefined)
var unit = msg.unit || '';  //The value to check (Must be a number)
var minLimit = msg.minLimit || null;  //The minimum acceptable value. Set to null if undefined
var maxLimit = msg.maxLimit || null; //The maximum acceptable value. Set to null if undefined

var Alerts = flow.get("Alerts"); //Fetch the active alerts
if ((minLimit !== null && value < minLimit) || (maxLimit !== null && maxLimit < value))  //Check if value is outside of acceptable range
{
  var ActiveAlerts = flow.get("ActiveAlerts"); //get the list of currently active alerts
  if (!Alerts.some(alert => alert === name)) //Check if the alert was active prevoiusly, if not: store it 
  {
    flow.set("SendEmail",true);
    Alerts.push(name);  
    flow.set("Alerts", Alerts); //adding the Alert to the ActiveAlert list
    ActiveAlerts.push("<b>[NEW] </b>" + name + " : " + "<b>" + valueText + unit + "</b>" + " [" + minLimit + " / " + maxLimit + "]");  //Alert HTML line for the email message
    flow.set("ActiveAlerts", ActiveAlerts);
  }
  else  //if alert was active before: Add to email
  {
    ActiveAlerts.push(name + " : " + "<b>" + valueText + unit + "</b>" + " [" + minLimit + " / " + maxLimit + "]");  //Alert HTML line for the email message
    flow.set("ActiveAlerts", ActiveAlerts);
  }

}
else  //if value is withing the acceptable range
{
  if (Alerts.some(alert => alert === name))  //Check if the alert was active prevoiusly, if it was: remove it
  {
    flow.set("SendEmail",true);
    flow.set("Alerts", Alerts.filter(function(alert) { return alert !== name; }));  //removing the Alert from the ActiveAlert list
    var RecoveredAlerts = flow.get("RecoveredAlerts");
    RecoveredAlerts.push("" + name + " : " + "<b>" + valueText + unit + "</b>" + " [" + minLimit + " / " + maxLimit + "]");  //Alert HTML line for the email message
    flow.set("RecoveredAlerts", RecoveredAlerts);
  }
}

